import React from 'react'
import {
    Link
} from "react-router-dom";

const buttonStyleBig = {
    fontFamily: 'Lato',
    border: 'none',
    height: '100px',
    width: '200px',
    borderRadius: '50px',
    fontSize: '20px',
    color: '#3b035f',
    backgroundImage: 'linear-gradient(to right, var(--main4) , var(--main5))'
};

export const Button = ({size="regular", to="/", value=""}) => {
    let btnStyle = buttonStyleBig
    if(size === "large") {
        btnStyle = buttonStyleBig
    }

    return (
        <Link class={size} to={to} ><button style={btnStyle}>{value}</button></Link>
    )
}

export default Button
