import React from 'react'
import Button from '../Components/Button'

const indexStyle = {
    display: 'flex',
    alignItems: 'center',
    height: '100%',
    flexDirection: 'row',
    justifyContent: 'space-around'
};

const mainMenuStyle = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '50px',
    background: 'var(--main2)',
    borderRadius: '25px',
    margin: 0
};

const mainIntroductionStyle = {
    border: '5px solid var(--main2)',
    borderRadius: '25px',
    height: '540px',
    padding: '10px',
    fontFamily: 'Lato',
    fontSize: '25px',
    maxWidth: '600px',
    fontWeight: '300',
    textAlign: 'right',
    display: 'flex',
    flexDirection: 'column-reverse'
};

class Index extends React.Component {
    render() {
        return (
            <div class="index">
                <div className="main_introduction">
                    <p>Crea tus propias trivias y compartela con tus amigos de la forma más fácil y divertida!</p>
                </div>
                <div class="main_menu">
                    <img class="item" src="logo.svg"/><h1>QuizMaster</h1>
                    <Button class="item" to="/jugar" value="Jugar" size="large"/>
                    <Button class="item" to="/crear" value="Crear" size="large"/>
                </div>
            </div>
        )
    }
}

export default Index
