import React from 'react'

const indexStyle = {
    display: 'flex',
    alignItems: 'center',
    height: '100%',
    flexDirection: 'row',
    justifyContent: 'space-around'
};

const mainMenuStyle = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '50px',
    background: 'var(--main2)',
    borderRadius: '25px',
    margin: 0
};

const mainIntroductionStyle = {
    border: '5px solid var(--main2)',
    borderRadius: '25px',
    height: '540px',
    padding: '10px',
    fontFamily: 'Lato',
    fontSize: '25px',
    maxWidth: '600px',
    fontWeight: '300',
    textAlign: 'right',
    display: 'flex',
    flexDirection: 'column-reverse'
};

const buttonStyle = {
    fontFamily: 'Lato',
    border: 'none',
    height: '100px',
    width: '200px',
    borderRadius: '50px',
    fontSize: '20px',
    color: '#3b035f',
    backgroundImage: 'linear-gradient(to right, var(--main4) , var(--main5))'
};

class Index extends React.Component {
    render() {
        return (
            <div class="index" style={indexStyle}>
                <div className="main_introduction" style={mainIntroductionStyle}>
                    <p>Crea tus propias trivias y compartela con tus amigos de la forma más fácil y divertida!</p>
                </div>
                <div class="main_menu" style={mainMenuStyle}>
                    <img class="item" src="logo.svg"/><h1>QuizMaster</h1>
                    <Link class="item" to="/jugar"><button>Jugar</button></Link>
                    <Link class="item" to="/crear"><button>Crear juego</button></Link>
                </div>
            </div>
        )
    }
}

export default Index
